import React, { useState, useEffect } from 'react';
import './App.css';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import foto from './foto.jpg';
import useFetch from './useFetch.js'

const url = "http://mcrnkovich.io/blog/wp-json/wp/v2/";

function About(props){
  const pic = <img id="about-foto" src={props.foto} alt="Not Found"></img>;
  const [about, isLoading] = useFetch(url+"pages/7", { content:{ rendered:"" }});

  return (
      <div>
        <h3 id="about" className="sectionHeader">About</h3>
        <div className="about">
          {pic}
          <div>
            {isLoading ? (<p>Loading...</p>):
              (<p dangerouslySetInnerHTML={{__html: about.content.rendered}}></p>)}
          </div>
        </div>
      </div>
    );
}

function ApiTestCall(props){

  const [count, setCount] = useState(0);
  const [posts, isLoading] = useFetch(url+"posts",[{content:{rendered:""}, title:{rendered:""}}] );

  function update(){  
    if (count>=posts.length-1){
      setCount(0);
    }
    else{
      setCount(count+1);
    }
  }
  function decrease(){  
    count<=0 ?
      setCount(posts.length-1):
      setCount(count-1);
  }
  return (
      <div>
        <h2 dangerouslySetInnerHTML={{__html: posts[count].title.rendered }}></h2>
        <div dangerouslySetInnerHTML={{__html: posts[count].content.rendered }}></div>
        <div className="row">
          <button onClick={decrease}>Previous</button>
          <button onClick={update}>Next</button>
        </div>
      </div>
    );
}

function Projects(props){
  return (
        <div className="row">
          <Card data={props.links} />
          <Card data={props.links} />
          <Card data={props.links} />
          <Card data={props.links} />
        </div>);
}

function Card(props){
  return (
    <div className="card">
      <a href={props.data.link}>
        <img className="thumbnail" src={foto} alt="missing file" />
        <h4>{props.data.name}</h4>
        <p>This is a description of the project</p>
      </a>
    </div>);
}


function ListLinkNewTab(props){
    return props.items.map((item)=>
      (<li>
        <a target="_blank" rel="noreferrer noopener" href={item.link}>
        {item.name}
        </a>
      </li>));
}

function ListLink(props){
    return props.items.map((item)=>
      (<li>
        <a href={item.link}>
        {item.name}
        </a>
      </li>));
}




function App() {
  const links = [{link:"https://www.github.com/mrcrnkovich", name:"GitHub"},
                  {link:"https://www.linkedin.com/in/michaelcrnkovich", name:"LinkedIn"}]
  const internalLinks = [{link:"/blog", name:"Blog"},
                  {link:"/about", name:"About"}];

  const HeaderLinks = (
              <ul id="menu">
                <ListLink items={internalLinks} />
                <ListLinkNewTab items={links} />
              </ul>);

  const proLinks = {link:"https://www.github.com/mrcrnkovich", name:"CRM"};
  const name ="about";
  return (
    <Router>
      <div className="App">
        <nav class="menu">
          <a id="homeLink" href="/"><h1>Michael Crnkovich</h1></a>
          {HeaderLinks}
        </nav>
        <div id="content">
          <Switch>
            <Route exact path="/">
              <h3>Landing Page</h3>
              <h3 id="projects">Projects</h3>
              <Projects links={proLinks} />
            </Route>
            <Route path={"/"+name}>
              <About foto={foto} />
            </Route>
            <Route path="/blog">
              <ApiTestCall />
            </Route>
          </Switch>
        </div>
      </div>
    </Router>
  );
}

export default App;
